                    {/* Sidebar/filters */}
                    <aside className="w-full lg:w-64 shrink-0">
                        <div className="bg-white rounded-xl shadow-sm p-6 sticky top-24">
                            <h2 className="font-medium text-lg mb-4">Filters</h2>
                            <div className="space-y-4">
                                <div>
