import {getAllCategories} from '@/services/categories';
import React, {Suspense} from 'react';
import {notFound} from 'next/navigation';
import SkeletonProductGrid from '@/components/product/SkeletonProductGrid';
import ProductGrid from '@/components/product/ProductGrid';
import {type CategoryItem} from '@/types/category';
import {type ProductFilter} from '@/types/product';
import HeaderBackgroundImage from '@/public/categoryHeaderBackgroundImage.webp';

interface CategoryParams {
    slug: string;
}

export async function generateStaticParams() {
    const categories: CategoryItem[] = await getAllCategories();

    // Function to extract all categories and subcategories recursively
    const extractAllCategories = (cats: CategoryItem[]): CategoryItem[] => {
        if (!cats || cats.length === 0) return [];

        let allCats: CategoryItem[] = [...cats];

        // Process each category's children recursively
        cats.forEach(cat => {
            if (cat.children && cat.children.length > 0) {
                allCats = [...allCats, ...extractAllCategories(cat.children)];
            }
        });

        return allCats;
    };

    // Get all categories including subcategories at any level
    const allCategories = extractAllCategories(categories);

    console.table(allCategories);
    // Generate params for all categories
    return allCategories.map((cat) => ({
        slug: cat.url_key,
    }));
}

const CategoryPage = async ({params}: { params: Promise<CategoryParams> }) => {
    const {slug} = await params;
    const categories = await getAllCategories();

    // Function to find a category by url_key in the entire category tree
    const findCategoryByUrlKey = (cats: CategoryItem[], urlKey: string): CategoryItem | null => {
        for (const cat of cats) {
            // Check if this is the category we're looking for
            if (cat.url_key === urlKey) {
                return cat;
            }

            // If not, check its children recursively
            if (cat.children && cat.children.length > 0) {
                const foundInChildren = findCategoryByUrlKey(cat.children, urlKey);
                if (foundInChildren) {
                    return foundInChildren;
                }
            }
        }

        return null;
    };

    // Find the category using our recursive function
    const category = findCategoryByUrlKey(categories, slug);

    if (!category) return notFound();

    const categoryFilter: ProductFilter = {
        "category_uid": {"eq": category.uid}
    }

    return (
        <div className="pt-24 sm:pt-28 md:pt-32">
            {/* Category header */}
            <div className="bg-gradient-to-r from-gray-50 to-white py-8 sm:py-12 mb-8 background-repeat" style={{background: "url(" + HeaderBackgroundImage.src + ")"}}>
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-gray-900 mb-4">
                        {category.name}
                    </h1>
                    <p className="text-gray-600 text-lg max-w-2xl">
                        Browse our collection of premium {category.name.toLowerCase()} products
                    </p>
                </div>
            </div>

            {/* Product grid section */}
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex flex-col lg:flex-row gap-8">
                    {/* Sidebar/filters */}
                    <aside className="w-full lg:w-64 shrink-0">
                        <div className="bg-white rounded-xl shadow-sm p-6 sticky top-24">
                            <h2 className="font-medium text-lg mb-4">Filters</h2>
                            <div className="space-y-4">
                                <div>
                                    <h3 className="text-sm font-medium mb-2">Price Range</h3>
                                    <div className="space-y-2">
                                        <div className="flex items-center">
                                            <input type="checkbox" id="price-1" className="mr-2" />
                                            <label htmlFor="price-1" className="text-sm">Under $50</label>
                                        </div>
                                        <div className="flex items-center">
                                            <input type="checkbox" id="price-2" className="mr-2" />
                                            <label htmlFor="price-2" className="text-sm">$50 - $100</label>
                                        </div>
                                        <div className="flex items-center">
                                            <input type="checkbox" id="price-3" className="mr-2" />
                                            <label htmlFor="price-3" className="text-sm">$100 - $200</label>
                                        </div>
                                        <div className="flex items-center">
                                            <input type="checkbox" id="price-4" className="mr-2" />
                                            <label htmlFor="price-4" className="text-sm">Over $200</label>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </aside>

                    {/* Product grid */}
                    <div className="flex-1 min-w-0">
                        <Suspense fallback={<SkeletonProductGrid/>}>
                            <ProductGrid filter={categoryFilter} />
                        </Suspense>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CategoryPage;

'use client'

import {
    NavigationMenu,
    NavigationMenuContent,
    NavigationMenuItem,
    NavigationMenuList,
    NavigationMenuTrigger
} from "@/components/ui/navigation-menu";
import { usePathname } from "next/navigation";
import React, { useState, useEffect } from "react";
import { cn } from "@/lib/utils";
import { CategoryItem } from "@/types/category";
import { CategoryMenuContent } from "./megamenu/CategoryMenuContent";

/**
 * Client-side megamenu component with backdrop effect
 */
export default function ClientSideMegaMenu({ categories }: { categories: CategoryItem[] }) {
    const pathname = usePathname();
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    // Effect to manage body scroll when menu is open
    useEffect(() => {
        if (isMenuOpen) {
            document.body.classList.add('overflow-hidden');
        } else {
            document.body.classList.remove('overflow-hidden');
        }

        return () => {
            document.body.classList.remove('overflow-hidden');
        };
    }, [isMenuOpen]);

    // Effect to add backdrop blur to main content only
    useEffect(() => {
        const mainElement = document.querySelector('main');
        if (mainElement) {
            if (isMenuOpen) {
                // Add blur and dimming effects to the main content
                mainElement.classList.add('backdrop-blur-md', 'brightness-50', 'transition-all', 'duration-300');

            } else {
                mainElement.classList.remove('backdrop-blur-md', 'brightness-50', 'transition-all', 'duration-300');
                const existingStyle = document.getElementById('megamenu-backdrop-style');
                if (existingStyle) {
                    existingStyle.remove();
                }
            }
        }
    }, [isMenuOpen]);

    return (
        <NavigationMenu onValueChange={(value) => setIsMenuOpen(!!value)}>
            <NavigationMenuList>
                {categories.map((category) => (
                    <NavigationMenuItem key={category.uid}>
                        <NavigationMenuTrigger
                            className={cn(
                                "text-sm font-medium bg-transparent",
                                pathname?.includes(`/category/${category.url_key}`)
                                    ? "text-primary"
                                    : "text-gray-700"
                            )}
                        >
                            {category.name}
                        </NavigationMenuTrigger>
                        <NavigationMenuContent className="w-[800px] lg:w-[950px] bg-white rounded-xl shadow-xl border border-gray-100 z-30">
                            <CategoryMenuContent category={category} activePath={pathname} />
                        </NavigationMenuContent>
                    </NavigationMenuItem>
                ))}
            </NavigationMenuList>
        </NavigationMenu>
    );
}

'use client'

import {
    NavigationMenu,
    NavigationMenuContent,
    NavigationMenuItem,
    NavigationMenuList,
    NavigationMenuTrigger
} from "@/components/ui/navigation-menu";
import { usePathname } from "next/navigation";
import React, { useState, useEffect, useCallback } from "react";
import { cn } from "@/lib/utils";
import { CategoryItem } from "@/types/category";
import { CategoryMenuContent } from "./megamenu/CategoryMenuContent";

/**
 * Client-side megamenu component with backdrop effect
 * Only handles interactive elements and effects, with content rendered by server components
 */
export default function ClientSideMegaMenu({ categories }: { categories: CategoryItem[] }) {
    const pathname = usePathname();
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    // Handle backdrop effect with a memoized function to improve performance
    const applyBackdropEffect = useCallback((isOpen: boolean) => {
        const mainElement = document.querySelector('main');
        if (!mainElement) return;

        if (isOpen) {
            // Add blur and dimming effects to the main content
            mainElement.classList.add('backdrop-blur-md', 'brightness-50', 'transition-all', 'duration-300');

            // Apply styles to any background images in the main element
            const style = document.createElement('style');
            style.id = 'megamenu-backdrop-style';
            style.textContent = `
                main::before {
                    content: '';
                    position: absolute;
                    inset: 0;
                    background-color: rgba(0,0,0,0.2);
                    backdrop-filter: blur(8px);
                    z-index: 5;
                    pointer-events: none;
                }
            `;
            document.head.appendChild(style);
        } else {
            mainElement.classList.remove('backdrop-blur-md', 'brightness-50', 'transition-all', 'duration-300');
            const existingStyle = document.getElementById('megamenu-backdrop-style');
            if (existingStyle) {
                existingStyle.remove();
            }
        }
    }, []);

    // Effect to manage body scroll and backdrop when menu is open
    useEffect(() => {
        if (isMenuOpen) {
            document.body.classList.add('overflow-hidden');
            applyBackdropEffect(true);
        } else {
            document.body.classList.remove('overflow-hidden');
            applyBackdropEffect(false);
        }

        return () => {
            document.body.classList.remove('overflow-hidden');
            applyBackdropEffect(false);
        };
    }, [isMenuOpen, applyBackdropEffect]);

    return (
        <NavigationMenu onValueChange={(value) => setIsMenuOpen(!!value)}>
            <NavigationMenuList>
                {categories.map((category) => (
                    <NavigationMenuItem key={category.uid}>
                        <NavigationMenuTrigger
                            className={cn(
                                "text-sm font-medium",
                                pathname?.includes(`/category/${category.url_key}`)
                                    ? "text-primary"
                                    : "text-gray-700"
                            )}
                        >
                            {category.name}
                        </NavigationMenuTrigger>
                        <NavigationMenuContent className="w-[800px] lg:w-[950px] bg-white rounded-xl shadow-xl border border-gray-100 z-30">
                            {/* CategoryMenuContent remains a server component */}
                            <CategoryMenuContent category={category} activePath={pathname} />
                        </NavigationMenuContent>
                    </NavigationMenuItem>
                ))}
            </NavigationMenuList>
        </NavigationMenu>
    );
}

import { CategoryItem } from "@/types/category";
import { CategoryMenuContent } from "./megamenu/CategoryMenuContent";
import MegaMenuWrapper from "./megamenu/MegaMenuWrapper";

/**
 * Server component that renders the megamenu
 * Uses the MegaMenuWrapper for client-side interactivity
 * while keeping most content server-rendered
 */
export default function MegaMenu({ categories }: { categories: CategoryItem[] }) {
  // This server component passes a render prop to the client wrapper
  // This technique allows server components to be consumed by client components
  return (
    <MegaMenuWrapper
      categories={categories}
      renderContent={(category, pathname) => (
        <CategoryMenuContent
          category={category}
          activePath={pathname}
        />
      )}
    />
  );
}

import { CategoryItem } from "@/types/category";
import MegaMenuWrapper from "./megamenu/MegaMenuWrapper";

/**
 * Server component that renders the megamenu
 * Uses the MegaMenuWrapper for client-side interactivity
 * while keeping most content server-rendered
 */
export default function MegaMenu({ categories }: { categories: CategoryItem[] }) {
  return (
    <MegaMenuWrapper categories={categories} />
  );
}

'use client';

import {
  Sheet,
  SheetContent,
  SheetTrigger,
  SheetTitle
} from '@/components/ui/sheet';
import { Button } from '@/components/ui/button';
import Link from 'next/link';
import { Menu, Search, X } from 'lucide-react';
import { CategoryItem } from '@/types/category';
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from '@/components/ui/accordion';
import { cn } from '@/lib/utils';
import { Input } from '@/components/ui/input';
import { isCategoryActive, useNavigation } from './navigation/navigation-utils';

export default function MobileMenu({ categories }: { categories: CategoryItem[] }) {
  const {
    pathname,
    isMenuOpen,
    setIsMenuOpen,
    isSearchOpen,
    setIsSearchOpen
  } = useNavigation(categories);

  return (
    <Sheet open={isMenuOpen} onOpenChange={setIsMenuOpen}>
      <SheetTrigger asChild>
        <Button variant="ghost" size="icon" className="md:hidden" aria-label="Menu">
          <Menu className="h-5 w-5" />
        </Button>
      </SheetTrigger>
      <SheetContent side="left" className="w-[85vw] max-w-xs px-0 pt-0 z-50">
        <SheetTitle className="sr-only">Navigation Menu</SheetTitle>
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="flex items-center justify-between p-4 border-b">
            <Link href="/" onClick={() => setIsMenuOpen(false)}>
              <h2 className="text-lg font-medium">Premium Store</h2>
            </Link>
            <Button variant="ghost" size="icon" onClick={() => setIsMenuOpen(false)} aria-label="Close menu">
              <X className="h-5 w-5" />
            </Button>
          </div>

          {/* Search toggle */}
          <div className="px-4 py-3 border-b">
            {isSearchOpen ? (
              <div className="flex items-center gap-2">
                <Input
                  type="search"
                  placeholder="Search products..."
                  className="flex-1 bg-transparent placeholder:text-gray-500"
                  autoFocus
                />
                <Button
                  variant="ghost"
                  size="icon"
                  onClick={() => setIsSearchOpen(false)}
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
            ) : (
              <Button
                variant="outline"
                className="w-full flex justify-between items-center"
                onClick={() => setIsSearchOpen(true)}
              >
                <span className="text-gray-500">Search products...</span>
                <Search className="h-4 w-4 text-gray-500" />
              </Button>
            )}
          </div>

          {/* Category navigation */}
          <div className="flex-1 overflow-y-auto py-2">
            <nav className="space-y-1">
              <Link 
                href="/"
                onClick={() => setIsMenuOpen(false)}
                className={cn(
                  "block px-4 py-2 text-base hover:bg-gray-50 transition-colors",
                  pathname === "/" ? "text-primary font-medium" : "text-gray-900"
                )}
              >
                Home
              </Link>

              {categories.map((category) => (
                <CategoryAccordion 
                  key={category.uid}
                  category={category}
                  pathname={pathname}
                  onLinkClick={() => setIsMenuOpen(false)}
                />
              ))}
            </nav>
          </div>

          {/* Footer */}
          <div className="border-t p-4">
            <Button variant="outline" className="w-full" onClick={() => setIsMenuOpen(false)}>
              <Link href="/account" className="w-full">My Account</Link>
            </Button>
          </div>
        </div>
      </SheetContent>
    </Sheet>
  );
}

function CategoryAccordion({ 
  category, 
  pathname, 
  level = 0,
  onLinkClick 
}: { 
  category: CategoryItem; 
  pathname: string; 
  level?: number;
  onLinkClick: () => void;
}) {
  const hasChildren = category.children && category.children.length > 0;
  const isActive = isCategoryActive(category, pathname);
  const padding = level > 0 ? { paddingLeft: `${(level + 1) * 0.75}rem` } : {};

  if (!hasChildren) {
    return (
      <Link
        href={`/category/${category.url_key}`}
        onClick={onLinkClick}
        className={cn(
          "block px-4 py-2 text-base hover:bg-gray-50 transition-colors",
          isActive ? "text-primary font-medium" : "text-gray-900"
        )}
        style={padding}
      >
        {category.name}
      </Link>
    );
  }

  return (
    <Accordion type="single" collapsible>
      <AccordionItem value={category.uid} className="border-none">
        <div className="flex items-center">
          <Link
            href={`/category/${category.url_key}`}
            onClick={onLinkClick}
            className={cn(
              "block flex-1 py-2 text-base hover:bg-gray-50 transition-colors pl-4",
              isActive ? "text-primary font-medium" : "text-gray-900"
            )}
            style={padding}
          >
            {category.name}
          </Link>
          <AccordionTrigger className="py-0 px-2" />
        </div>
        <AccordionContent className="pb-0 pt-1">
          {category.children?.map((child) => (
            <CategoryAccordion
              key={child.uid}
              category={child}
              pathname={pathname}
              level={level + 1}
              onLinkClick={onLinkClick}
            />
          ))}
        </AccordionContent>
      </AccordionItem>
    </Accordion>
  );
}

