import Image from "next/image";
import { type ProductItem } from "@/types/product";

export default function ProductCard({ product }: { product: ProductItem }) {
    const price = product.price_range.minimum_price.final_price;
    const swatches =
        product.configurable_options?.find(
            (opt) => opt.attribute_code === "color"
        )?.values ?? [];

    return (
        <div className="flex flex-col items-start w-[250px] hover:shadow-lg transition-shadow duration-200 ease-in-out p-4">
            {/* Product Image */}
            <a
                href={`/product/${product.url_key}`}
                className="block w-full h-[300px] rounded-lg overflow-hidden relative z-10"
            >
                {product.small_image?.url ? (
                    <Image
                        src={product.small_image.url}
                        alt={product.small_image.label || product.name}
                        width={240}
                        height={300}
                        className="object-contain w-full h-full"
                    />
                ) : (
                    <div className="w-full h-full bg-gray-200" />
                )}
            </a>

            {/* Name */}
            <h3 className="mt-2 text-sm font-medium text-gray-800 truncate w-full">
                {product.name}
            </h3>

            {/* Price */}
            <p className="mt-1 text-gray-600 text-sm">
                {price.currency} {price.value.toFixed(2)}
            </p>

            {/* Swatches */}
            {swatches.length > 0 && (
                <div className="mt-3 flex space-x-2">
                    {swatches.map((swatch, idx) => (
                        <div
                            key={idx}
                            title={swatch.label}
                            className="w-6 h-6 rounded-full border border-gray-300 overflow-hidden z-10"
                            style={{
                                backgroundColor: swatch.swatch_data?.value || "transparent",
                            }}
                        >
                            {swatch.swatch_data?.thumbnail && (
                                <Image
                                    src={swatch.swatch_data.thumbnail}
                                    alt={swatch.label}
                                    width={24}
                                    height={24}
                                    className="object-cover"
                                />
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

import Image from "next/image";
import { type ProductItem } from "@/types/product";

export default function ProductCard({ product }: { product: ProductItem }) {
    const price = product.price_range.minimum_price.final_price;
    const swatches =
        product.configurable_options?.find(
            (opt) => opt.attribute_code === "color"
        )?.values ?? [];

    return (
        <div className="flex flex-col items-start w-[250px] hover:shadow-lg transition-shadow duration-200 ease-in-out p-4">
            {/* Product Image */}
            <a
                href={`/product/${product.url_key}`}
                className="block w-full h-[300px] rounded-lg overflow-hidden relative z-10"
            >
                {product.small_image?.url ? (
                    <Image
                        src={product.small_image.url}
                        alt={product.small_image.label || product.name}
                        width={240}
                        height={300}
                        className="object-contain w-full h-full"
                    />
                ) : (
                    <div className="w-full h-full bg-gray-200" />
                )}
            </a>

            {/* Name */}
            <h3 className="mt-2 text-sm font-medium text-gray-800 truncate w-full">
                {product.name}
            </h3>

            {/* Price */}
            <p className="mt-1 text-gray-600 text-sm">
                {price.currency} {price.value.toFixed(2)}
            </p>

            {/* Swatches */}
            {swatches.length > 0 && (
                <div className="mt-3 flex space-x-2">
                    {swatches.map((swatch, idx) => (
                        <div
                            key={idx}
                            title={swatch.label}
                            className="w-6 h-6 rounded-full border border-gray-300 overflow-hidden z-10"
                            style={{
                                backgroundColor: swatch.swatch_data?.value || "transparent",
                            }}
                        >
                            {swatch.swatch_data?.thumbnail && (
                                <Image
                                    src={swatch.swatch_data.thumbnail}
                                    alt={swatch.label}
                                    width={24}
                                    height={24}
                                    className="object-cover"
                                />
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

import GET_FILTERED_PRODUCTS_QUERY from "@/graphql/queries/products/get_filtered_products.graphql";
import { GraphQLResponse } from "@/types/apollo";
import { ProductItem } from "@/types/product";
import { print } from "graphql";
import { type ProductFilter } from "@/types/product";
import GET_PRODUCTS_BY_CATEGORY_QUERY from "@/graphql/queries/products/get_products_by_category.graphql";

interface ProductsResponse {
    products: {
        total_count: number;
        items: ProductItem[];
    };
}

/**
 * Fetch products by category UID
 */
const getProductsByCategory = async (category_uid: string): Promise<ProductItem[]> => {
    "use cache";
    const queryString = print(GET_PRODUCTS_BY_CATEGORY_QUERY);

    try {
        const res = await fetch(process.env.MAGENTO_GRAPHQL_ENDPOINT as string, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                query: queryString,
                variables: { cat: category_uid },
            }),
        });

        if (!res.ok) {
            console.error("Error fetching products:", res.statusText);
            return [];
        }

        const { data, errors }: GraphQLResponse<ProductsResponse> = await res.json();

        if (errors) {
            console.error("GraphQL errors:", errors);
            return [];
        }

        return data?.products?.items ?? [];
    } catch (error) {
        console.error("Error fetching products:", error);
        return [];
    }
};

/**
 * Fetch products by arbitrary Magento filters
 */
const getFilteredProducts = async (filters: ProductFilter): Promise<ProductItem[]> => {
    "use cache";
    const queryString = print(GET_FILTERED_PRODUCTS_QUERY);

    try {
        const res = await fetch(process.env.MAGENTO_GRAPHQL_ENDPOINT as string, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                query: queryString,
                variables: { filter: filters },
            }),
        });

        if (!res.ok) {
            console.error("Error fetching filtered products:", res.statusText);
            return [];
        }

        const { data, errors }: GraphQLResponse<ProductsResponse> = await res.json();

        if (errors) {
            console.error("GraphQL errors:", errors);
            return [];
        }

        return data?.products?.items ?? [];
    } catch (error) {
        console.error("Error fetching filtered products:", error);
        return [];
    }
};

const getProductByUrlKey = async (url_key: string): Promise<ProductItem | null> => {
    "use cache";
    const queryString = print(GET_FILTERED_PRODUCTS_QUERY);

    const filter: ProductFilter = {
        "url_key":  { eq: url_key }
    }

    try {
        const res = await fetch(process.env.MAGENTO_GRAPHQL_ENDPOINT as string, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                query: queryString,
                variables: { filter: filter },
            }),
        });

        if (!res.ok) {
            console.error("Error fetching product:", res.statusText , "   " ,url_key);
            return null;
        }

        const { data, errors }: GraphQLResponse<ProductsResponse> = await res.json();

        if (errors) {
            console.error("GraphQL errors:", errors);
            return null;
        }

        return data?.products?.items[0] ?? null;
    } catch (error) {
        console.error("Error fetching filtered products:", error);
        return null;
    }
};

export { getProductsByCategory, getFilteredProducts, getProductByUrlKey};

